using System;using System.Collections.Generic;using System.ComponentModel;using System.Data;using System.Drawing;using System.IO;using System.Linq;using System.Management;using System.Text;using System.Threading.Tasks;using System.Windows.Forms;using WindowsFormsApp1.DeviceComputes;using WindowsFormsApp1.Math.Statistics;using WindowsFormsApp1.Utils;using GASS.CUDA;using MathNet.Numerics.Statistics;using MathNet;using static MathNet.Numerics.Random.RandomExtensions;namespace WindowsFormsApp1{    public partial class Form1 : Form    {        private List<double[]> _fileArrays;        private volatile bool isRedAlready = false;        private string filePath;        private string outputFolder;        private IComputeDevice device;        private int _shiftWidth, _batchSize;                        public Form1()        {            InitializeComponent();            computeDevice.SelectedIndex = 0;        }        private void ButtonLoadClick(object sender, EventArgs e)        {            _fileArrays = new List<double[]>();            isRedAlready = false;            using (var ofd = new System.Windows.Forms.OpenFileDialog())            {                if (ofd.ShowDialog() == System.Windows.Forms.DialogResult.OK)                {                    TryInit(ofd.FileName);                    filePath = ofd.FileName;                    FillDataGrid();                }                else                {                    MessageBox.Show("Не выбран файл!");                }            }        }        private void TryInit(string filePath)        {            Task.Run(() =>            {                var lines = File.ReadLines(filePath).Skip(4);                char fileSeparator = GetCurrentSeparator(lines.First());                InitArrays(lines.Count(), lines.First().Split(fileSeparator).Length);                VolatileRead(lines, fileSeparator);                isRedAlready = true;            });        }        void InitArrays(int rowLength, int rows)        {            for (int i = 0; i < rows; i++)            {                _fileArrays.Add(new double[rowLength]);            }        }        private char GetCurrentSeparator(string line1)        {            foreach (var sep in " \t,")            {                if (line1.Contains(sep) && line1.Split(sep).Length > 1)                {                    return sep;                }            }            throw new FileLoadException("Bad file format!");        }        private void VolatileRead(IEnumerable<string> lines, char separator)        {            for (int i = 0; i < lines.Count(); i++)            {                var splitted = lines.ElementAt(i).Split(separator);                for (int j = 0; j < splitted.Length; j++)                {                    _fileArrays.ElementAt(j)[i] = Double.Parse(splitted[j]);                }            }        }        private void FillDataGrid()        {            string[,] rows = new string[,]{ // 1   2     3     4                 {"1","AAA","BBB","10"},//1                {"2","CCC","DDD","20"},//2                {"3","ZZZ","YYY","30"},//3                {"4","LLL","MMM","40"},                {"5","NNN","TTT","50"},                {"6","WWW","RRR","60"},                {"7","GGG","PPP","70"}//7            };            // rows.GetLength(0) return length of the first D (7)            // rows.GetLength(1) return length of the second D            for(int i = 0; i < rows.GetLength(0); i++)// array rows            {                string[] row = new string[rows.GetLength(1)];                for(int j = 0; j < rows.GetLength(1); j++)                {                    row[j] = rows[i, j];                }                dataGridView1.Rows.Add(row);            }        }        private void InitComputeDevice()        {            if (int.TryParse(BatchSizeBox.Text, out _batchSize) && int.TryParse(shiftBox.Text, out _shiftWidth))            {                switch (computeDevice.SelectedIndex) // CPU Parallel                 {                    case 0:                        device = new CpuComputeDevice(outputFolder, filePath);                        break;                    case 1:                        device = new OpenBLASCompute(outputFolder, filePath);                        break;                    case 2:                        device = new CUDACompute(outputFolder, filePath);                        break;                }            }        }        private void CalculateClick(object sender, EventArgs e)        {            InitComputeDevice();            var watch = System.Diagnostics.Stopwatch.StartNew();            device.ShiftCompute(_fileArrays, _shiftWidth, _batchSize);            watch.Stop();            var elapsedMs = ((double) watch.ElapsedMilliseconds / 1000);            Console.WriteLine("Time of exection: " + elapsedMs);        }        private void computeDevice_SelectedIndexChanged(object sender, EventArgs e)        {            if (computeDevice.SelectedIndex == 2)            {                if ((new CUDA(true).Devices.Length != 0))                {                    ManagementObjectSearcher myVideoObject =                        new ManagementObjectSearcher("select * from Win32_VideoController");                    foreach (ManagementObject obj in myVideoObject.Get())                    {                        DeviceListBox.Text = $"Device: {obj["Name"]}\n";                    }                }                else                {                    DeviceListBox.Text = "No CUDA device found!";                }            }            else             {                ManagementObjectSearcher myProcessorObject =                     new ManagementObjectSearcher("select * from Win32_Processor");                foreach (ManagementObject obj in myProcessorObject.Get())                {                    DeviceListBox.Text = $"Device: {obj["Name"]} \n";                }                            }        }        private void PickOutputFolderClick(object sender, EventArgs e)        {            using (var fbd = new System.Windows.Forms.FolderBrowserDialog())            {                if (fbd.ShowDialog() == System.Windows.Forms.DialogResult.OK)                {                    outputFolder = fbd.SelectedPath;                }                else                {                    MessageBox.Show("Папка не выбрана!");                }            }        }    }}